import React, { useState } from 'react';
import Card from '../UI/Card';
import Button from '../UI/Button';
import styles from './ItemTramo.module.css';
import { GiFullMotorcycleHelmet } from 'react-icons/gi';


const ItemTramo = (props) => {

const [MotosDisponibles, setMotosDisponibles ] = useState(8);

const motosDisponibleHandler = () => {
    var UsoMoto = MotosDisponibles - 1;
    if(UsoMoto < 0){return};
    setMotosDisponibles(UsoMoto);
};


    return (
        <Card  className={styles.Card}>
         <h1 className={styles.title}> Turno {props.title} </h1>
         <Button className={styles.Button} onClick={motosDisponibleHandler}> <GiFullMotorcycleHelmet size={32}/> &nbsp;&nbsp; {MotosDisponibles} Motos Disponibles</Button>
          </Card>
    );
};

export default ItemTramo;
