import React, { useState } from "react";
import Card from "../UI/Card";
import Button from "../UI/Button";
import styles from "./ItemTramo.module.css";
import { GiFullMotorcycleHelmet } from "react-icons/gi";

const ItemTramo = (props) => {
  const [MotosDisponibles, setMotosDisponibles] = useState(8);
  const [motosAgotadas, setMotosAgotadas]= useState(false);
  const [recursoSolicitado, setRecursoSolicitado] = useState(false);

  const motosDisponibleHandler = () => {
    var UsoMoto = MotosDisponibles - 1;
    setRecursoSolicitado(true);
    if (UsoMoto === 0) {
      setMotosDisponibles(UsoMoto);
      setMotosAgotadas(true); 
      setRecursoSolicitado(false); 
      return;
    }
    if(UsoMoto < 0){return};
    setMotosDisponibles(UsoMoto);
     };

  return (
    <Card className={`${styles.Card} ${motosAgotadas === true ? styles.rojo : ""} ${recursoSolicitado === true ? styles.verde : ""}`}>
      <h1 className={styles.title}> Turno {props.title} </h1>
      <Button
        className={styles.Button}
        onClick={motosDisponibleHandler}
      >
        
        <GiFullMotorcycleHelmet size={32} /> &nbsp;&nbsp; {MotosDisponibles}
        &nbsp; Motos Disponibles
      </Button>
    </Card>
  );
};

export default ItemTramo;
