  {
    id: "25",
    hora: "8:00 P.M.",
  },
];

const ListaRecursos = () => {


  const [recursosPedidos, SetRecursosPedidos] = useState(
    Array.from({ length: 10 }, () =>                            //Cantidad  de Usuarios
      Array.from({ length: LISTA_HORAS.length }, () => false)   //Cantidad de tramos
    )
  );

  const recursoPedidoHandler = (id) =>{
    SetRecursosPedidos(()=> !recursosPedidos[0][id]);
  }

  const listaHoras = LISTA_HORAS.map((hora) => {
    return <ItemTramo key={hora.id} title={hora.hora} recursoPedido={recursosPedidos[0][0]} onClickHandler={recursoPedidoHandler}/>;
  });

  return <section className={styles.lista}>{listaHoras}</section>;
};

export default ListaRecursos;
